import { useHistory } from 'react-router-dom'

const Login = () => {


    let history = useHistory();

    let initialState = {
        'email' : '',
        'password' : '',
    } 

    let [ data , setData ] = useState(initialState);

    const changeState = (e) => {
        setData(prevstate => { 
            return {...prevstate , [e.target.name] : e.target.value }
        });
    }

    const login = async (e) => {

        e.preventDefault();

